(198, '裕华明珠', '["2"]', 4160, '鼎城区大湖路与永安路交汇', NULL, 7, '6路，10路，47路，102路', '["1","5"]', 1, 0, 0, 0, 1, 0, 0, 0, 5, 1, '["2","4"]', 0, '<p>&nbsp; &nbsp; 裕华明珠位于鼎城区大湖路与永安路交汇处，由湖南裕华房地产开发有限公司开发，公司成立于2005年，注册资金1000万，曾经开发过裕华豪苑，锦江苑，裕华苑，华南小区，锦绣江南，锦绣南苑等。<br />&nbsp; &nbsp; 小区占地面积10465.3㎡，总建筑面积33478㎡.绿化率35%容积率3.2，楼间距45米，车位比例达1:0.7。分两期开发，一期两栋多层，二期两栋25层高层。周边配套医疗方面：鼎城区人民医院，善卷医院。<br />&nbsp; &nbsp; 学校：常沅小学，严家岗小学，鼎城一中，鼎城九中。文娱：体育广场，善卷公园，羽毛球馆，游泳中心。市场有桥南大市场，久光国际,建材市场，家电市场，财富广场。路过公交有6路，10路，47路等。<br /></p>', 0, '400-0736-600', '鼎城区大湖路与永安路交汇', '10465', '33478', '84', '35', '3.2', '福吉物业', '0.00', '区九中，常沅小学', NULL, '', '/upfiles/images/201406/091518211708.jpg', '', '[{"id":"1926","url":"\\/upfiles\\/images\\/201406\\/091518211708.jpg","localname":"\\u9879\\u76ee\\u793a\\u610f\\u56fe_\\u526f\\u672c.jpg","title":""}]', 1, '2014-06-09 15:08:41', 1, '108', 1, 0, 0, '', 0, 0, '', 0, 0, 0, '待定', 0, 0, '8966', 1, 0, 0, 0, 0, 0, '86-- 118', '[]', '整体已封顶', '16', NULL, '[]', '', '111.698225,29.008588,18', '启明星培训训学校、严家岗沅小、常沅小学、鼎城九中、鼎城一中 ', '小龙门幼儿园、小红帽幼儿园、启明星培训训学校、未来之星幼儿园', '久光国际、桥南市场、财富广场', '建设银行、农村信用社、农业银行、中国银行银行、中国工商银行', '江南医院、妇幼保健院', '47路、6路、9路、21路、10路', '善卷公园、常德长南汽车总站、鼎城区儿童乐园 鼎城区游泳馆、鼎城区体育馆、 上通加油站、华京大酒店 ', NULL, '70', '8:30——19:00', '209', '', '', '湖南集美房地产营销策划有限公司', '湖南广源建设工程有限公司', '', '', '["2013035"]', '["1","2","3"]', 8549, 0, '0', '万', NULL, NULL, 0, '', '', '', '2014-08-02', '2016-05-28', '240', '1.30', '1.30', '1.30', '0.00', '32088', '1345', '0736-2667776', '', 0, '', 111.698225, 29.008588, 0, '', '3年', ''),
(199, '鼎欣嘉豪', '["4","6"]', 4000, '鼎城常沅路与花溪路交汇处', NULL, 7, '公交车8路、16路、21路、47路，快速公交H1，紧邻桃花源大桥。', '["1","5"]', 1, 0, 0, 0, 1, 0, 0, 0, 3, 1, '["2","4"]', 0, '<p>&nbsp; &nbsp; 鼎欣嘉豪坐落在常沅路与花溪路交汇处，距离桥南小圆盘300米，总建筑面积约7万平方米，共有住宅496套，尚有现房120套，单套面积从41至158平米不等，户型从一室一厅到四室两厅一应俱全。<br />&nbsp; &nbsp; 小区内有自建幼儿园，周边临近鼎城一中，淮阳中学，善卷中学，花船庙小学等各类学校，距桥南市场1200米，周边公共设施配套齐全，交通便利，多条公交车线路直达市区。<br />&nbsp; &nbsp; 鼎欣嘉豪全现房入市，小区园林景观绿化及亮化工程新颖别致，环境优美，基础设施功能配套完善，商业裙楼周边有超大规模的休闲广场，专业的物业管理，先进的安防系统，倡导生态健康人文、积极、乐观的新型生活方式，是江南城区集现代化智能于一体的复合型高尚社区，是您放心置业的理想小区。</p>', 0, '400-0736-600', '常德市鼎城区常沅路', '17068.44', '70784.48', '83%', '39.9', '3.9', '鼎城鸿锦物业服务公司', '0.00', '鼎城一中、淮阳中学，善卷小学、花船庙小学', NULL, '', '/upfiles/images/201406/231509048844.jpg', '/upfiles/images/201406/231508346151.jpg', '[{"id":"2222","url":"\\/upfiles\\/images\\/201406\\/231509048844.jpg","localname":"DSC01783_\\u526f\\u672c.jpg","title":""},{"id":"2221","url":"\\/upfiles\\/images\\/201406\\/231508502066.jpg","localname":"DSC01795_\\u526f\\u672c.jpg","title":""},{"id":"2220","url":"\\/upfiles\\/images\\/201406\\/231508346151.jpg","localname":"\\u9e1f\\u77b0\\u56fe 006_\\u526f\\u672c.jpg","title":""},{"id":"2225","url":"\\/upfiles\\/images\\/201406\\/231509566810.jpg","localname":"DSC01773_\\u526f\\u672c.jpg","title":""},{"id":"2224","url":"\\/upfiles\\/images\\/201406\\/231509427803.jpg","localname":"DSC01776_\\u526f\\u672c.jpg","title":""},{"id":"2223","url":"\\/upfiles\\/images\\/201406\\/231509207232.jpg","localname":"DSC01781_\\u526f\\u672c.jpg","title":""}]', 1, '2014-06-23 14:48:34', 8, '104', 1, 95, 0, '一房一价', 0, 0, '', 0, 0, 0, '', 0, 0, '8522', 0, 0, 0, 0, 1, 0, '130', '[]', '全面竣工', '17', NULL, '[]', '', '111.681229,29.013626,19', '鼎城一中、淮阳中学，善卷小学、花船庙小学', '鼎欣幼儿园', '桥南市场、步步高、久光国际、华星电器', '农村信用社、工商银行、邮政银行、建设银行', '常德市六医院、妇幼保健院', '紧邻桃花源大桥、一桥。 有47路、8路、16路、21路、快速公交直达市区', '物流园、汽车总站', NULL, '住宅70年，商铺40年', '', '173', '', '常德市鼎城玉龙房地产开发有限公司', '', '常德蓝天建筑有限公司', '广东省珠海泰基建筑设计工程有限公司', '', '["2011054-2011057"]', '["1","2","3"]', 10035, 0, '0', '万', NULL, NULL